export default function PartnerLogos({ variant = 'static', title = 'Powered by Industry Leaders' }) {
  const scrollRef = useRef(null);

  useEffect(() => {
    if (variant === 'marquee' && scrollRef.current) {
      const scrollContainer = scrollRef.current;
      let animationId;
      let scrollPos = 0;

      const animate = () => {
        scrollPos += 0.5;
        if (scrollPos >= scrollContainer.scrollWidth / 2) {
          scrollPos = 0;
        }
        scrollContainer.scrollLeft = scrollPos;
        animationId = requestAnimationFrame(animate);
      };

      animationId = requestAnimationFrame(animate);
      return () => cancelAnimationFrame(animationId);
    }
  }, [variant]);

  if (variant === 'marquee') {
    return (
      <div className="py-12 bg-gray-50 dark:bg-gray-900/50 overflow-hidden">
        <div className="max-w-7xl mx-auto px-6 mb-8">
          <p className="text-center text-sm font-medium text-gray-500 dark:text-gray-400 uppercase tracking-wider">
            {title}
          </p>
        </div>
        <div 
          ref={scrollRef}
          className="flex gap-16 overflow-hidden whitespace-nowrap"
          style={{ scrollBehavior: 'auto' }}
        >
          {/* Double the logos for seamless loop */}
          {[...logos, ...logos].map((logo, idx) => (
            <div 
              key={idx}
              className="flex-shrink-0 w-12 h-12 text-gray-400 dark:text-gray-500 hover:text-gray-600 dark:hover:text-gray-300 transition-colors duration-300"
              title={logo.name}
            >
              {logo.svg}
            </div>
          ))}
        </div>
      </div>
    );
  }

  // Static grid variant
  return (
    <div className="py-16 bg-gray-50 dark:bg-gray-900/50">
      <div className="max-w-7xl mx-auto px-6">
        <p className="text-center text-sm font-medium text-gray-500 dark:text-gray-400 uppercase tracking-wider mb-10">
          {title}
        </p>
        <div className="flex flex-wrap justify-center items-center gap-12 md:gap-16">
          {logos.map((logo, idx) => (
            <div 
              key={idx}
              className="w-10 h-10 md:w-12 md:h-12 text-gray-400 dark:text-gray-500 hover:text-gray-600 dark:hover:text-gray-300 transition-colors duration-300 cursor-pointer"
              title={logo.name}
            >
              {logo.svg}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
